export const  Fetch = async (url: string, method: "POST" | "GET", headers: HeadersInit | undefined, body: BodyInit | null | undefined) => {
    try{
        const req = await fetch(url, {
            method: method,
            mode: "cors",
            headers: headers,
            body: body
        })

        const res = req.json()

        if(!res) throw Error

        return res

    } catch(Error) {
        console.log(Error)
        window.location.replace("/")

    }
}
